select P1.username,P1.phone,P1.address,P1.email,P2.name unit,P3.notes,P4.name position from user_info P1 LEFT JOIN org_unit P2 ON P1.unit=P2.id LEFT JOIN salary P3 ON P1.salary=P3.id LEFT JOIN org_post P4 ON P1.position = P4.id
P1.username like concat(concat('%',#{username}),'%')
limit #{currentIndex},#{pageSize}

